import React, { useState, useEffect } from 'react';
import RepetitionExercise from './components/RepetitionExercise';
import DurationExercise from './components/DurationExercise';
import './App.css';

// New component for Running Exercise with stopwatch functionality
const RunningExercise = ({ goBack }) => {
  const [isRunning, setIsRunning] = useState(false);
  const [lapTimes, setLapTimes] = useState([]);
  const [elapsedTime, setElapsedTime] = useState(0);

  useEffect(() => {
    let interval;
    if (isRunning) {
      const startTime = Date.now() - elapsedTime;
      interval = setInterval(() => {
        setElapsedTime(Date.now() - startTime);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isRunning, elapsedTime]);

  const startStopwatch = () => {
    setIsRunning(true);
  };

  const recordLap = () => {
    setLapTimes([...lapTimes, elapsedTime]);
  };

  const formatTime = (time) => {
    const seconds = Math.floor(time / 1000);
    return `${seconds} seconds`; // Simple formatting for demonstration
  };

  return (
    <div>
      <h3>Running Exercise</h3>
      {!isRunning && <button onClick={startStopwatch}>Start</button>}
      <button onClick={recordLap}>Record Lap</button>
      <div>
        <h4>Laps</h4>
        {lapTimes.map((time, index) => (
          <div key={index}>{`Lap ${index + 1}: ${formatTime(time)}`}</div>
        ))}
      </div>
      <button onClick={goBack}>Go Back</button>
    </div>
  );
};

function App() {
  const [currentScreen, setCurrentScreen] = useState('menu');

  const exerciseMenu = [
    { name: 'Push Ups', type: 'Repetition' },
    { name: 'Plank', type: 'Duration' },
    { name: 'Running', type: 'Running' }
  ];

  const renderExercise = (exercise) => {
    switch (exercise.type) {
      case 'Repetition':
        return <RepetitionExercise name={exercise.name} goBack={() => setCurrentScreen('menu')} />;
      case 'Duration':
        return <DurationExercise name={exercise.name} goBack={() => setCurrentScreen('menu')} />;
      case 'Running':
        return <RunningExercise goBack={() => setCurrentScreen('menu')} />;
      default:
        return <div>Select an exercise from the menu</div>;
    }
  };

  return (
    <div className="App">
      {currentScreen === 'menu' ? (
        <div>
          <div><h2>Let's Exercise!</h2></div>
          {exerciseMenu.map((exercise, index) => (
            <button key={index} onClick={() => setCurrentScreen(() => renderExercise(exercise))}>
              {exercise.name}
            </button>
          ))}
        </div>
      ) : (
        currentScreen
      )}
    </div>
  );
}

export default App;
